import { useEffect } from "react"
import { useState } from "react"
import axios from "axios"
import { Link, useNavigate } from "react-router-dom"
import Footer from "./Footer";
import { initializeApp } from "firebase/app";
import { getAuth, signOut } from "firebase/auth"
import User from "./user";
import Pagination from "./Pagination";

function Home() {
  const [data, setData] = useState([])
  const [value, setValue] = useState("")// search purpose
  const [sort, setSort] = useState("")// sorting purpose
  const [count, setCount] = useState(0) // cart number count

  let Currentuser = User()

  useEffect(() => {
    fetch("http://localhost:3008/Products")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search = async (e) => {
    e.preventDefault()
    return await axios.get(`http://localhost:3008/Products?q=${value}`)
      .then((res) => {
        setData(res.data)
      })
      .catch((err) => {
        console.log("err")
      })
  }

  let options = ["title", "price"]
  const sorting = async (e) => {
    let abc = e.target.value
    setSort(abc)
    return await axios.get(`http://localhost:3008/Products?_sort=${abc}&_order=asc`)
      .then((res) => {
        setData(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const Addcart = (id) => {
    fetch("http://localhost:3008/Products/" + id)
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        console.log(resp)
        fetch("http://localhost:3008/AddedCart/",
