import { useEffect } from "react"
import { useState } from "react"
import axios from "axios"
import { Link,useNavigate} from "react-router-dom"
import Footer from "./Footer";
import { initializeApp } from "firebase/app";
import {getAuth,signOut} from "firebase/auth"
import User from "./user";

function Home() {
  const [data, setData] = useState([])
  const[value, setValue] = useState("")// search purpose
  const [sort, setSort] = useState("")// sorting purpose
  const[count,setCount]=useState(0) // cart number count

  let Currentuser=User()

  useEffect(() => {
    fetch("http://localhost:3008/Products")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search=async(e)=>{
    e.preventDefault()
    return await axios.get(`http://localhost:3008/Products?q=${value}`)
    .then((res)=>{
      setData(res.data)
    })
    .catch((err)=>{
      console.log("err")
    })
  }

  let options =["title","price"]
  const sorting=async(e)=>{
    let abc=e.target.value
    setSort(abc)
    return await axios.get(`http://localhost:3008/Products?_sort=${abc}&_order=asc`)
    .then((res)=>{
      setData(res.data)
    })
    .catch((err)=>{
      console.log(err)
    })
  }

  const Addcart=(id)=>{
    fetch("http://localhost:3008/Products/"+ id)
    .then((res)=>{
      return res.json()
    })
    .then((resp)=>{
      console.log(resp)
      fetch("http://localhost:3008/AddedCart/",
      {
        method:"POST",
        headers:{"Content-type":"application/json"},
        body:JSON.stringify(resp)
      })
      .then(()=>{
        alert("Items added into cart")
      })
    })
  }

  useEffect(()=>{
    fetch("http://localhost:3008/AddedCart")
    .then((res)=>{
      return res.json()
    })
    .then((resp)=>{
        setCount(resp)
    })
  })

  let navigate=useNavigate()

    const firebaseConfig = {
        apiKey: "AIzaSyCBrdNQODqSaqrbLWb0mFxqh0cNVvq_SNw",
        authDomain: "kitchen-1629b.firebaseapp.com",
        projectId: "kitchen-1629b",
        storageBucket: "kitchen-1629b.appspot.com",
        messagingSenderId: "900572452211",
