import { useEffect, useState } from "react";
import Footer from "./Footer";
import Pagination from "./Pagination";
import { Link } from "react-router-dom";

function AddCart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3008/AddedCart")
      .then((r) => r.json())
      .then((res) => {
        setData(res);
        console.log(res);
      });
  }, []);

  const updateQuantity = (id, newQuantity) => {
    const updatedData = data.map((item) =>
      item.id === id ? { ...item, Quantity: newQuantity } : item
    );

    setData(updatedData);

    // Update the quantity on the server
    fetch(`http://localhost:3008/AddedCart/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ Quantity: newQuantity }),
    });
  };

  const Del = (id) => {
    fetch(`http://localhost:3008/AddedCart/${id}`, {
      method: "DELETE",
    });
    alert("Are you sure to Delete ?!!");
    // Remove the item from the state
    const updatedData = data.filter((item) => item.id !== id);
    setData(updatedData);
  };

  //pagination
  const [page, setPage] = useState(1);
  const [records, setRecords] = useState(4);

  let lrecord = page * records; // last record = 4
  let frecord = lrecord - records; // first record = 0
  let show = data.slice(frecord, lrecord);

  const updatepages = (num) => {
    setPage(num);
  };

  const Prev = () => {
    setPage(page - 1);
  };

  const Next = () => {
    setPage(page + 1);
  };

  return (
    <div className="container-fluid">
      <span className="d-flex justify-content-around mt-2">
        <h3>Your Cart</h3>
        <Link to="/home" className="btn btn-secondary">
          Back to Home
        </Link>
      </span>
      <div className="row row-cols-1 row-cols-md-4 g-3 mt-3">
        {show.map((e) => (
          <div className="col" key={e.id}>
            <div className="card h-100">
              <div className="card-body">
                <img
                  src={e.Img}
                  className="card-img-top"
                  width="100%"
                  height="200px"
                  alt="..."
                />
                <h5 className="card-title">Title:{e.title}</h5>
                <h5 className="card-title">Amount:${e.price}</h5>
                <div className="d-flex">
                  <h5 className="card-title">Quantity:{e.Quantity}</h5>
                  <div className="ms-2">
                    <button
                      className="btn btn-secondary btn-sm"
                      onClick={() =>
                        updateQuantity(e.id, Math.max(e.Quantity - 1, 0))
                      }
                    >
                      -
                    </button>
                    <button
                      className="btn btn-secondary btn-sm ms-1"
                      onClick={() => updateQuantity(e.id, e.Quantity + 1)}
                    >
                      +
                    </button>
                  </div>
                </div>
                <button
                  className="btn btn-danger mt-2"
                  onClick={() => Del(e.id)}
                >
                  Delete <i className="bi bi-file-x mx-1"></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <Pagination
        total={data.length}
        records={records}
        update={updatepages}
        prev={Prev}
        next={Next}
      />
      <div>
        <Footer />
      </div>
    </div>
  );
}

export default AddCart;
