function Pagination({total,records,update}){
    let n=Math.ceil(total/records)
    let pages=[]
    for (let i=1;i<=n;i++){
        pages.push(i)
    }
    console.log(pages)
   
    return(
        <div>
            <ul className="pagination mx-5">
                {pages.map((p)=>(
                    <li className="page-item">
                        <a href="#" className="page-link" onClick={()=>{update(p)}}>{p}</a>
                    </li>
                ))}
            </ul>
        </div>
    )
}
export default Pagination;
