import { useEffect } from "react"
import { useState } from "react"
import axios from "axios"
import { Link, useNavigate } from "react-router-dom"
import Footer from "./Footer";
import { initializeApp } from "firebase/app";
import { getAuth, signOut } from "firebase/auth"
import User from "./user";
import Pagination from "./Pagination";

function ProductDisplay() {
  const [data, setData] = useState([])
  const [value, setValue] = useState("")// search purpose
  const [sort, setSort] = useState("")// sorting purpose
  const [count, setCount] = useState(0) // cart number count

  let Currentuser = User()

  useEffect(() => {
    fetch("http://localhost:3008/Products")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search = async (e) => {
    e.preventDefault()
    return await axios.get(`http://localhost:3008/Products?q=${value}`)
      .then((res) => {
        setData(res.data)
      })
      .catch((err) => {
        console.log("err")
      })
  }

  let options = ["title", "price"]
  const sorting = async (e) => {
    let abc = e.target.value
    setSort(abc)
    return await axios.get(`http://localhost:3008/Products?_sort=${abc}&_order=asc`)
      .then((res) => {
        setData(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }

  const Addcart = (id) => {
    fetch("http://localhost:3008/Products/" + id)
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        console.log(resp)
        fetch("http://localhost:3008/AddedCart/",
          {
            method: "POST",
            headers: { "Content-type": "application/json" },
            body: JSON.stringify(resp)
          })
          .then(() => {
            alert("Items added into cart")
          })
      })
  }

  useEffect(() => {
    fetch("http://localhost:3008/AddedCart")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setCount(resp)
      })
  }, [])

  let navigate = useNavigate()

  const firebaseConfig = {
    apiKey: "AIzaSyCBrdNQODqSaqrbLWb0mFxqh0cNVvq_SNw",
    authDomain: "kitchen-1629b.firebaseapp.com",
    projectId: "kitchen-1629b",
    storageBucket: "kitchen-1629b.appspot.com",
    messagingSenderId: "900572452211",
    appId: "1:900572452211:web:e3c42e77e2d0c66016a106",
    measurementId: "G-KC1160MZC5"
  };

  const app = initializeApp(firebaseConfig);
  const reg = getAuth()
  const submitdata = (e) => {
    e.preventDefault()

    signOut(reg)
      .then(() => {
        alert(" Logged Out successfully")
        navigate("/login")
      })
      .catch(() => {
        alert("error")
      })
  }

  //pagination
  const [page, setPage] = useState(1)
  const [records, setRecords] = useState(4)

  let lrecord = page * records //last record= 4
  let frecord = lrecord - records //first record =0
  let show = data.slice(frecord, lrecord)

  const updatepages = (num) => {
    setPage(num)
  }

  const Prev = () => {
    setPage(page - 1)
  }

  const Next = () => {
    setPage(page + 1)
  }
  return (
    <div>
        <nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
            Dropdown
          </a>
          <ul class="dropdown-menu">
            <li><a class="dropdown-item" href="#">Action</a></li>
            <li><a class="dropdown-item" href="#">Another action</a></li>
            <li><hr class="dropdown-divider"/></li>
            <li><a class="dropdown-item" href="#">Something else here</a></li>
          </ul>
        </li>
        <li class="nav-item">
          <a class="nav-link disabled" aria-disabled="true">Disabled</a>
        </li>
      </ul>
      <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
    </div>
  )
}

export default ProductDisplay;
