import { type } from "@testing-library/user-event/dist/type"
import { useEffect } from "react"
import { useState } from "react"
import axios from "axios"

function Home() {
  const [data, setData] = useState([])
  const[value, setValue] = useState("")// search purpose
  const [sort, setSort] = useState("")// sorting purpose
  const[count,setCount]=useState(0) // cart number count

  useEffect(() => {
    fetch("http://localhost:3008/Products")
      .then((res) => {
        return res.json()
      })
      .then((resp) => {
        setData(resp)
      })
  }, [])

  const search=async(e)=>{
    e.preventDefault()
    return await axios.get(`http://localhost:3008/Products?q=${value}`)
    .then((res)=>{
      setData(res.data)
    })
    .catch((err)=>{
      console.log("err")
    })
  }

  let options=["Title","Amount"]
  const sorting=async(e)=>{
    let value=e.target.value
    setSort(value)
    return await axios.get(`http://localhost:3008/Products?_sort=${value}&_order=asc`)
    .then((res)=>{
      setData(res.data)
    })
    .catch((err)=>{
      console.log(err)
    })
  }

  const Addcart=(id)=>{
    fetch("http://localhost:3008/Products/"+ id)
    .then((res)=>{
      return res.json()
    })
    .then((resp)=>{
      console.log(resp)
      fetch("http://localhost:3008/AddedCart",
      {
        method:"POST",
        headers:{"Content-type":"application/json"},
        body:JSON.stringify(resp)
      })
      .then(()=>{
        alert("Items added into cart")
      })
    })
  }

  useEffect(()=>{
    fetch("http://localhost:3008/AddedCart")
    .then((res)=>{
      return res.json()
    })
    .then((resp)=>{
        setCount(resp)
    })
  })

  return (
    <div>
      <nav class="navbar navbar-expand-lg bg-body-info fs-5">
        <div class="container-fluid">
          <a class="navbar-brand" href="#"><img src="https://www.zarla.com/images/zarla-our-kitchen-1x1-2400x2400-20210607-g7q69ybhqxvvy7rtwvvj.png?crop=1:1,smart&width=250&dpr=2" height="100px" alt="" /></a>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0 mx-5">
              <form class="d-flex" onSubmit={search}>
                <input class="form-control ms-5 me-1" type="text" onChange={(e)=>setValue(e.target.value)} placeholder="Search your product" />
                <button class="btn btn-outline-success" type="submit">Search</button>
              </form>
            </ul>
            <span class="navbar-text mx-5 d-flex">
              <div class="nav-item">
                <a class="nav-link mx-5" href="#">
                  <i class="bi bi-cart-plus-fill h3"></i>
                  <span class="position-absolute top-7 start-70 fs-6 translate-middle badge rounded-pill bg-danger">
                     {count.length}
                    <span class="visually-hidden"></span>
                  </span></a>
              </div>
              <a class="nav-link"><i class="bi bi-person-circle" style={{ fontSize: "20px" }}></i>User</a>
            </span>
            <div class="d-flex" role="search">
              <button class="btn btn-outline-success" type="submit">Logout</button>
            </div>
          </div>
        </div>
      </nav>
      <div className=" row mx-2 container-fluid" id="body">
      <div className="col-md-3 mt-5" id="left">
          <div>
          <form class="form-control">
            <select class="form-select mt-3" aria-label="Default select example">
              <option selected>Shop by Concern</option>
              <option value="1">One</option>
              <option value="2">Two</option>
              <option value="3">Three</option>
            </select><br />
            <select class="form-select" aria-label="Default select example">
              <option selected>Shop by Category</option>
              <option value="1">One</option>
              <option value="2">Two</option>
              <option value="3">Three</option>
            </select>
            <br />
            <select class="form-select" value={sort} onChange={sorting} aria-label="Default select example">
              <option selected>---Sort---</option>
              <option value="1">{options.map((item)=>(
                <option value="2">{item}</option>
              ))}</option>
              {/* <option value="1">Title</option> */}
              {/* <option value="2">Amount</option> */}
              {/* <option value="3">Three</option> */}
            </select>
            <br />
            <input type="text" class="form-control" placeholder="shop all" />
            <br/>
          </form >
          </div>
          <br/><br/>
          <div>
          <form className="form-control">
            <div className="container">
              <label for="customRange2" class="form-label">Example range</label>
              <input type="range" class="form-range" min="0" max="4" id="customRange2" /><br /><br />
              <label for="customRange2" class="form-label">Example range</label>
              <input type="range" class="form-range" min="0" max="3" id="customRange2" />
              <br />
              <br />
            </div>
          </form>
          </div>

        </div>
        <div className="col-md-9" id="right">
        <h3 className="text-center">Product Display</h3>
          <div className="row row-cols-1 row-cols-md-3 g-3">
            {data.map((e) => (
              <div className="col">
                <div className="card h-100">
                  <div className="card-body">
                  <img src={e.Img} class="card-img-top" width="100%" height="200px" alt="..."/>
                    <h5 class="card-title">Title:{e.title}</h5>
                    <h5 class="card-title">Amount:${e.price}</h5>
                    <h5 class="card-title">Quantity:{e.Quantity}</h5>
                    <button className="btn btn-success" onClick={()=>Addcart(e.id)}>Add to Cart<i class="bi bi-bag-plus mx-1"></i></button>
                  </div>
                </div>
               </div>
            ))}

          </div>
        </div>
        
       

    </div>
    
    </div>

  )
}



export default Home;
