import { useEffect, useState } from "react"
import Footer from "./Footer";


 function AddCart(){
    const [data,setData]=useState([])
    // const[total,setTotal]=useState(0)
    useEffect(()=>{
        fetch("http://localhost:3008/AddedCart")
        .then((r)=>{
                return r.json()
        })
        .then((res)=>{
            setData(res)
            console.log(res)
        })
    },[])

    const Del=(e)=>{
        fetch("http://localhost:3008/AddedCart/"+ e,{
            method:"DELETE"
        })
        alert("Are you sure to Delete ?!!")
        window.location.reload()
    }
    return(
        <div className="container-fluid">
             {/* <div className="col-md-9" id="right"> */}
        <h3 className="text-center">Your Cart</h3>
          <div className="row row-cols-1 row-cols-md-4 g-3">
            {data.map((e) => (
              <div className="col">
                <div className="card h-100">
                  <div className="card-body">
                  <img src={e.Img} class="card-img-top" width="100%" height="200px" alt="..."/>
                    <h5 class="card-title">Title:{e.title}</h5>
                    <h5 class="card-title">Amount:${e.price}</h5>
                    <h5 class="card-title">Quantity:{e.Quantity}</h5>
                    <button className="btn btn-danger" onClick={()=>Del(e.id)}> Delete<i class="bi bi-file-x mx-1"></i></button>
                  </div>
                </div>
               </div>
            ))}

          </div>
        {/* </div> */}
        <div>
                    <Footer/>
                </div>
        
        </div>
        
    )
}

export default AddCart;
